glsl3.vert:
precision mediump float;
in vec2 a_position;
in vec2 a_texCoord;
out vec2 v_uv;
layout(std140) uniform Constant {
  vec4 u_buffer0;
  vec4 u_buffer1;
  mat4 u_projection;
};
vec4 vert () {
  vec2 worldPos = a_position * u_buffer1.xy + u_buffer1.zw;
  vec2 clipSpace = worldPos / u_buffer0.xy * 2.0 - 1.0;
  vec4 screenPos = u_projection * vec4(clipSpace, 0.0, 1.0);
  v_uv = a_texCoord;
  return screenPos;
}
void main() { gl_Position = vert(); }

glsl3.frag:
precision mediump float;
in vec2 v_uv;
layout(std140) uniform Factor {
  float u_percent;
};
uniform sampler2D mainTexture;
vec4 frag () {
  vec4 color = texture(mainTexture, v_uv);
  float percent = clamp(u_percent, 0.0, 1.0);
  color.xyz *= percent;
  return color;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

